con:testCase.con:setupScript:
import groovy.sql.Sql
//Information about the database
def gdb = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue("Database");
def database;
if (gdb == null) {
database =
testRunner.testCase.testSuite.project.getPropertyValue("Database");
}else{
database = gdb;
}
def username = testRunner.testCase.testSuite.project.getPropertyValue("Username")
def password = testRunner.testCase.testSuite.project.getPropertyValue("Password")
//access to the database
def sql = Sql.newInstance(database, username, password)

//DELETE BY ENDPOINT_NAME TO HANDLE POST FAILS WHICH PREVENT ENDPOINT ID TRANSFERS
sql.execute("delete from FD.ENDPOINT where ENDPOINT_NAME  like 'TESTENDPOINTP%'")
sql.execute("delete from FD.ENDPOINT where ENDPOINT_NAME  like 'TESTENDPOINTSEARCHP%'")
sql.execute("delete from FD.ENDPOINT where ENDPOINT_NAME  like 'TestEndpointSearchP%'")

